mapper.select:
  	SELECT F_FROM_MEMBER_NO, F_TO_TARGET_NO 
	FROM FOLLOW f 
	WHERE F_FROM_MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM BOOKMARK
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}
